d (min-width:56.25em)and (orientation:landscape) {     .carousel-mystery-bounty img{       width: calc(96% / 3);       top:15%;       margin-left: 0;     }      .wrapper-mystery-bounty .arrow-mystery-bounty{   bottom: -10%;  }   .wrapper-mystery-bounty{   margin: 2em auto;   width: 90%;   height: auto;   min-height: 40vh;   align-items: center;   }      .carousel-mystery-bounty{       min-height: 40vh;      }     .carousel-item-2{     z-index: 2;     position: absolute;     transform: translateX(100%) scale(1.5);     transition: all 0.8s ease-out;     box-shadow: -2px 5px 33px 6px rgba(0, 0,0,0.35);                 }     .carousel-item-1{       position: absolute;       transform: scale(1);       z-index: 1;       left: 0;       opacity: 0.6;       transition: all 0.8s ease-out;           }      .carousel-item-3{       position: absolute;       transform: scale(1);       z-index: 1;        right: 0;       opacity: 0.6;       transition: all 0.8s ease-out;     }      .carousel-item-4{       position: absolute;       transform: scale(1);       z-index: 1;        right: 50%;       opacity: 0;       transition: all 0.8s ease-out;     }   }    @media screen and (min-width:75em){      .wrapper-mystery-bounty .arrow-mystery-bounty{       bottom: -15%;          }      }     @media screen and (min-width:91.875em) and (orientation:landscape){     .wrapper-mystery-bounty .arrow-mystery-bounty{       bottom: -5%;          }     .wrapper-mystery-bounty{       min-height: 45vh;     }   } </style>  <script>
// middle text animation 
const sectionTextMiddle = document.querySelector("#mystery-bounty-title h2")
const strText = sectionTextMiddle.textContent;
 const splitText = strText.split("")
 sectionTextMiddle.textContent = ""
 for(let i=0; i< splitText.length; i++) {
    sectionTextMiddle.innerHTML+= "<span>" + splitText[i] + "</span>"
 }
let char = 0;
let timer = setInterval(onTick, 50)
function onTick() {
const span = sectionTextMiddle.querySelectorAll("span")[char]
span.classList.add("fade-mystery-bounty")
char++;
if(char === splitText.length) {
    complete();
    return;
}
}

function complete() {
clearInterval(timer);
timer = null;
}
//  carousel
const carousel = document.querySelector(".carousel-mystery-bounty"),
firstImg = carousel.querySelectorAll("img")[0],
arrowIcons = document.querySelectorAll(".arrow-mystery-bounty");
allImgs=carousel.querySelectorAll("img")

    let isDragStart = false, isDragging = false, prevPageX, prevScrollLeft, positionDiff;
    let classIndex=0;
    const updateGallery = (direction) => {
        allImgs.forEach(el => {
          el.classList.remove("carousel-item-1")
          el.classList.remove("carousel-item-2")
          el.classList.remove("carousel-item-3")
          el.classList.remove("carousel-item-4")
        })
      
        if(direction === "left") {
          classIndex--;
        } else {
          classIndex++;
        }
      
        if(classIndex === 4) {
          classIndex = 0;
        } else if(classIndex === -1) {
          classIndex = 3;
        }
      
        Array.from(allImgs).forEach((el, i) => {
          const classes = ["carousel-item-1", "carousel-item-2", "carousel-item-3", "carousel-item-4" ]
          el.classList.add(classes[(classIndex + i) % 4])
        })
      }
      
      arrowIcons.forEach(icon => {
        icon.addEventListener("click", (e) => {
          let firstImgWidth = firstImg.clientWidth + 14; 
         carousel.scrollLeft += icon.id === "left" ? -firstImgWidth : firstImgWidth;
          updateGallery(icon.id)
        });
      });
      
    
   
    
    const autoSlide = () => {
     
        if(carousel.scrollLeft - (carousel.scrollWidth - carousel.clientWidth) > -1 || carousel.scrollLeft <= 0) return;
    
        positionDiff = Math.abs(positionDiff); 
        let firstImgWidth = firstImg.clientWidth + 14;

        let valDifference = firstImgWidth - positionDiff;
    
        if(carousel.scrollLeft > prevScrollLeft) { 
            return carousel.scrollLeft += positionDiff > firstImgWidth / 3 ? valDifference : -positionDiff;
        }
      
        carousel.scrollLeft -= positionDiff > firstImgWidth / 3 ? valDifference : -positionDiff;
    }
    
    const dragStart = (e) => {
    
        isDragStart = true;
        prevPageX = e.pageX || e.touches[0].pageX;
        prevScrollLeft = carousel.scrollLeft;
    }
    
    const dragging = (e) => {
       
        if(!isDragStart) return;
        e.preventDefault();
        isDragging = true;
        carousel.classList.add("dragging");
        positionDiff = (e.pageX || e.touches[0].pageX) - prevPageX;
        carousel.scrollLeft = prevScrollLeft - positionDiff;
       
    }
    
    const dragStop = () => {
        isDragStart = false;
        carousel.classList.remove("dragging");
    
        if(!isDragging) return;
        isDragging = false;
        autoSlide();
    }
    
    carousel.addEventListener("mousedown", dragStart);
    carousel.addEventListener("touchstart", dragStart);
    
    document.addEventListener("mousemove", dragging);
    carousel.addEventListener("touchmove", dragging);
    
    document.addEventListener("mouseup", dragStop);
    carousel.addEventListener("touchend", dragStop);
</script>     </div> </div>               </div>      </div> </div></div> <div class="rich-text">            <div class="rich-text-component pt-medium pb-medium">     <div class="text"><h2><b><span style="color: rgb(0,192,255);">The best bit:</span></b></h2> <p><br> <span style="color: rgb(255,255,255);">In our Mystery Bounty tournaments, you get more chances to win awesome cash prizes than ever before. You don't even need to finish in the money to win big. Just reach the Mystery Bounty stage, knock out an opponent from the game and you win their bounty. It's that simple!<br> We've got tons of special mystery bounty events at all buy-in levels for you to enjoy, every day.<br> <br> Hunt some bounties today in the&nbsp;</span><a href="/openclient/"><span style="color: #00c0ff;"><u>tournaments lobby</u></span></a><span style="color: rgb(255,255,255);">.</span></p></div> </div></div> <div class="video">                                    <div class="video-component responsive-video-container">         <iframe class="responsive-iframe" src="https://www.youtube.com/embed/rHeI-uL_bG4" width="100%" height="100%" allow="autoplay; fullscreen; picture-in-picture" frameborder="0" allowfullscreen="">         </iframe>     </div>   </div> <div class="cta">           <div onmousedown="return false;" onselectstart="return false;" class="cta-component d-flex justify-content-center pt-medium pb-large">                     <div class="cta-template cta-secondary">         <a href="/openclient/" aria-label="Let's go bounty hunting">                 <span class="label bold">Let's go bounty hunting</span>                          </a>     </div>  </div></div>               </div>      </div> </div></div>               </div>          </div>               </div> </div></div><?php include_once ($_SERVER["DOCUMENT_ROOT"]."/footer.php"); ?>