document.addEventListener("DOMContentLoaded", function () {
  setClickEventOnBoxes();
});

function showDetails(cardId, card) {
  const arrow = card.querySelector(".arrow");
  const targetBox = document.getElementById("box-extended-" + cardId);

  const targetBoxVisible = targetBox.style.display === "block";

  // Hide all targetBoxes
  document
    .querySelectorAll(".details-container .description-box-extended")
    .forEach((descBox) => {
      descBox.style.display = "none";
    });

  // Reset all arrows
  document
    .querySelectorAll(".descriptive-box .readMoreLink .readMore .arrow")
    .forEach((readMore) => {
      readMore.style.transform = "none";
    });

  document
    .querySelectorAll(".descriptive-box .greyBgFix")
    .forEach((greyBgFix) => {
      greyBgFix.classList.remove("greyBgFixColor");
    });

  if (!targetBoxVisible) {
    targetBox.style.display = "block";
    arrow.style.transform = "rotate(180deg)";
    card.querySelector(".greyBgFix").classList.add("greyBgFixColor");

    const cardRow = Math.floor(cardId / 3);

    if (window.innerWidth < 900) {
      const detailsContainers = document.querySelectorAll(".details-container");
      if (detailsContainers.length > cardRow) {
        card.insertAdjacentElement("afterend", targetBox);
      } else {
        document.querySelector(".resp-boxes").appendChild(targetBox);
      }
    }
  } else {
    targetBox.style.display = "none";
    arrow.style.transform = "none";
    card.querySelector(".greyBgFix").classList.remove("greyBgFixColor");
  }
}

function setClickEventOnBoxes() {
  document.querySelectorAll(".descriptive-box").forEach((card, i) => {
    card.addEventListener("click", () => showDetails(i, card));
  });
}
</script>     </div> </div> <div class="rich-text">            <div class="rich-text-component pt-medium">     <div class="text"><p>With an epic lineup of fun poker events, there’s something to suit everyone at SNOWMEN. Come play in the biggest online poker festival of 2023! No tents or raincoats needed…</p></div> </div></div>               </div>      </div> </div></div> <div class="dark-promotion">  <div class="dark-promotion-component">     <div class="dark-promotion-container">                   </div> </div></div> <div class="container responsivegrid">  <div class="pt-medium pt-medium">     <div class="container-wrapper main-container">                             <div id="container-54df6972fa" class="cmp-container">                  <div class="cta">           <div onmousedown="return false;" onselectstart="return false;" class="cta-component d-flex justify-content-center">                     <div class="cta-template cta-secondary">         <a href="/openclient/" target="_blank" aria-label="I want in">                 <span class="label bold">I want in</span>                          </a>     </div>  </div></div> <div class="rich-text">            <div class="rich-text-component pt-small">     <div class="text"><p>Funded players only • Promotion is valid until&nbsp;31.12.2023 • country restrictions apply • Bonus Valid for 30 days • Tickets must be claimed within 7 days • Rebuys do not count towards the tournament challenge • Min. stakes of 1c / 2c apply for cash challenge • Min. odds of 1/2 (1.5) apply for sport challenge • Winning hands applies to pocket cards only • To withdraw bonus and related wins players must accumulate 3 bonus points for each $1 in 30 days • Freeroll ticket(s) valid for 7 days • Prizes will be granted within 72 hours •&nbsp;$8,888 Snowmen weekly Freeroll runs until&nbsp;31.12.2023 •&nbsp;$8,888 Snowmen weekly Freeroll is a Multiflight tournament •&nbsp;$8,888 Snowmen weekly Freeroll is limited to 18,000 players • <a href="/terms/snowme